mapper.update:
UPDATE goods SET product_id = #{productId}, `name` = #{name}, number = #{number}, title = #{title}, price = #{price}, scroll = #{scroll}, hot = #{hot}, keyword = #{keyword}, description = #{description},
logo_url = #{logoUrl}, logo_real_url = #{logoRealUrl},
content = #{content}, update_time = CURRENT_TIMESTAMP, browse_times = #{browseTimes} WHERE id = #{id}

mapper.select:
		
		WHERE
		1 = 1
        
            AND t.id = ${tradeId}
		
        
            AND b.id = ${brandId}
		
        
            AND p.id = ${productId}
		
		
			
	            AND g.price >= ${lowestPrice}
			
		
		
			
	            AND g.price <= ${highestPrice}
			
		
        
			AND g.keyword like '%${keyword}%'
		
	    ORDER BY g.update_time DESC
	    
			LIMIT #{start}, #{limit}

		SELECT COUNT(*)
		FROM
			goods g
		LEFT JOIN product p ON g.product_id = p.id
		LEFT JOIN brand b ON p.brand_id = b.id
		LEFT JOIN trade t ON b.trade_id = t.id
		WHERE
		1 = 1
        
            AND t.id = ${tradeId}
		
        
            AND b.id = ${brandId}
		
        
            AND p.id = ${productId}
		
		
			
	            AND g.price >= ${lowestPrice}
			
		
		
			
	            AND g.price <= ${highestPrice}
			
		
        
			AND g.keyword like '%${keyword}%'
